import React, { Component } from "react";
import {
  Button,
} from "@material-ui/core";
import { QRCode } from 'react-qrcode-logo';
import Dropzone, {useDropzone} from 'react-dropzone';
import lgmeeaa from "./logo_aposento nuevo-01.png"

export class FormPersonalDetails extends Component {
  continue = e => {
    e.preventDefault();
    this.props.nextStep();
  };
  back = e => {
    e.preventDefault();
    this.props.prevStep();
  };
  constructor() {
    super();
    this.onDrop = (files) => {
        this.getAsText(files[0]);
        this.props.handleChangefile(files)
    }
    this.state = {
      disabled: false,
      files: []
    };
  }
  


  getAsText=(fileToRead)=> {
    var reader = new FileReader();
    // Read file into memory as UTF-8      
    reader.readAsText(fileToRead);
    // Handle errors load
    reader.onload = this.loadHandler;
    reader.onerror = this.errorHandler;
  } 

  
  loadHandler=(event)=>{
    var csv = event.target.result;
    


  }

  errorHandler(evt) {
    if(evt.target.error.name == "NotReadableError") {
        alert("Canno't read file !");
    }
  }

  render() {
    const { values, handleChange } = this.props;

    const files =values.files.map((file,index) => (
        <li key={file.name}>
          {file.name}
        </li>
      ));
    return (
        <section className="formg" ref={this.props.refe} >
            <h3 className="textokoiform">Guarda esta informaci√≥n</h3>
            <QRCode logoImage={lgmeeaa}  logoHeight={10} value="https://github.com/gcoro/react-qrcode-logo" />
       
        <Button
          style={{
            background: "#1b0578",
            color: "#FFFFFF",
            marginRight: "1em",
            width: "40%",
            height:"10%"
          }}
          label="Back"
          onClick={this.back}
        >
          Back
        </Button>
        <Button
          style={{background: "#fa7b25", color: "#ffff",
            width: "40%", height:"10%"
          }}
          label="Continue"
          onClick={this.continue}
        >
          {" "}
          Continue
        </Button>
      </section>
    );
  }
}

export default FormPersonalDetails;
